mapper.select:
SELECT perform_id, perform_date, perform_user_id, perform_total_amount, u.user_name as perform_user_name FROM market_perform p LEFT JOIN sys_user u ON p.perform_user_id=u.user_id
order by perform_date asc
LIMIT #{start_index},#{rows}

select sum(perform_total_amount) as total_sell_amount
from market_perform
WHERE perform_user_id=#{user_id}
